const news = [] //insert an array of strings in extremely specific format for this to work. I won't be using this, but if the game ever has to get expanded...
let month = -1;
function monthToDay(string) {
    let day = parseInt(string.split(' ')[1]);
    if (day = NaN) return MONTH_START[month+1];
    return MONTH_START[month]+parseInt(string.split(' ')[1]);
}

function convertNews() { //convert news placed through a few find and replace filters into the form it will be in the code.
    let str = "";
    for (let i=0;i<news.length;i++) {
        if (!news[i].includes(" ")) {
            month++;
            if (month > 11) month=0;
            continue;
        } else if (!news[i].includes("–")) {
            let temp = news[i];
            let j=1;
            for (j=1;true;j++) {
                if (news[i+j].includes("–") || news[i+j].length < 15) break;
                str += '["'+temp+" – "+ news[i+j].replace(/"+/g,'\\"') +'",'+monthToDay(temp)+'],\n';
            }
            i += j-1;
        } else str += '["'+ news[i].replace(/"+/g,'\\"') +'",'+monthToDay(news[i])+'],\n';
    }
    return str;
}